import React from "react";
import axios from "axios";

export class ItemService {
    baseUrl = "http://localhost:8080";
    
   // const d = document;
    $table = d.querySelector(".responsive");
    $form = d.querySelector("new-task-form");
    $template = d.getElementById("task-template").content;
    $fragment = d.createDocumentFragment();
    async getAll() {
      try {
        const response = await fetch(this.baseUrl + "/tasks");
        const json = await response.json();
  
        if (!response.ok)
          throw { status: response.status, statusText: response.statusText };
  
        json.forEach((el) => {
          this.$template.d.querySelector(".text").textContent = el.task;
  
          let $clone = d.importNode($template, true);
          this.$fragment.appendChild($clone);
        });
        this.$table.querySelector(".responsive").appendChild($fragment);
      } catch (err) {
        let message = err.statusText || "Error";
        this.$table.inserAdjacentHtml(
          "afterend",
          `<p><b>Error${err.status}: ${message}</b></p>`
        );
      }
    }
}
